import React from "react";
import styled from "styled-components";

const Overlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5); /* Î∞òÌà¨Î™Ö Î∞∞Í≤Ω */
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 9999;
`;

const ModalContainer = styled.div`
  display: flex;
  flex-direction: column;
  background-color: #fff;
  padding: 20px;
  border-radius: 8px;
  width: 800px;
  height: 1000px;
  min-height: 300px;
  max-height: 600px;
  overflow-y: auto;
  position: relative; /* Î∂ÄÎ™® Í∏∞Ï§ÄÏúºÎ°ú ÏúÑÏπò ÏÑ§Ï†ï */
`;

const CloseButton = styled.button`
  position: absolute;
  top: 10px; /* ÏÉÅÎã®ÏóêÏÑú 10px Îñ®Ïñ¥Ïßê (Î™®Îã¨ ÎÇ¥Î∂Ä) */
  right: 10px; /* Ïö∞Ï∏°ÏóêÏÑú 10px Îñ®Ïñ¥Ïßê (Î™®Îã¨ ÎÇ¥Î∂Ä) */
  background: white;
  color: black; /* "X"Í∞Ä Ïûò Î≥¥Ïù¥ÎèÑÎ°ù Ìù∞ÏÉâÏúºÎ°ú ÏÑ§Ï†ï */
  font-size: 20px;
  border: none; /* Í∏∞Î≥∏ ÌÖåÎëêÎ¶¨ Ï†úÍ±∞ */
  border-radius: 50%; /* ÏõêÌòï Î≤ÑÌäºÏúºÎ°ú Ïä§ÌÉÄÏùºÎßÅ (ÏÑ†ÌÉù ÏÇ¨Ìï≠) */
  width: 30px; /* Î≤ÑÌäº ÌÅ¨Í∏∞ */
  height: 30px; /* Î≤ÑÌäº ÌÅ¨Í∏∞ */
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  padding: 0; /* Ìå®Îî© Ï†úÍ±∞ */
  &:hover {
    background: #333; /* Ìò∏Î≤Ñ Ïãú ÏÉâÏÉÅ Î≥ÄÍ≤Ω (ÏÑ†ÌÉù ÏÇ¨Ìï≠) */
  }
`;

export default function Modal({ onClose, children, className }) {
  return (
    <Overlay>
      <ModalContainer className={className}>
        <CloseButton onClick={() => {
            console.log("X Î≤ÑÌäº ÌÅ¥Î¶≠Îê®!"); // üîç ÎîîÎ≤ÑÍπÖ ÏΩîÎìú Ï∂îÍ∞Ä
            onClose(); // onClose Ìò∏Ï∂ú
          }}
        >
          X</CloseButton>
        {children}
      </ModalContainer>
    </Overlay>
  );
}
