import React, { useRef, useState, useEffect } from 'react';
import './Canvas.css';  // Í∏∞Ï°¥ CSS ÌååÏùºÏùÑ Î∂àÎü¨ÏòµÎãàÎã§

const Canvas = ({ backgroundImage,imageSrc }) => {
  const canvasRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [context, setContext] = useState(null);
  const [color, setColor] = useState('black');
  const [lineWidth, setLineWidth] = useState(5);
  const [isErasing, setIsErasing] = useState(false);
  const [fileName, setFileName] = useState('my_drawing'); // ÌååÏùºÎ™Ö ÏûÖÎ†• ÏÉÅÌÉú
  const [isWhiteBackground, setIsWhiteBackground] = useState(false); // Î∞∞Í≤ΩÏÉâ ÏÑ§Ï†ï

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    setContext(ctx);
    ctx.lineJoin = 'round';
    ctx.lineCap = 'round';
    ctx.strokeStyle = color;
    ctx.lineWidth = lineWidth;
  }, [color, lineWidth]);
  ///////////Ïù¥ÏïÑÎûòÏú†Ï¶àÏù¥ÌéôÌä∏ ÏàòÏ†ï
  useEffect(() => {
    if (backgroundImage && context) {
      const img = new Image();
      img.crossOrigin = 'Anonymous';
      img.src = `http://localhost:8081/files/view/${backgroundImage}`; // ÏÑ†ÌÉùÎêú Ïù¥ÎØ∏ÏßÄ URL
      img.onload = () => {
        context.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height); // Í∏∞Ï°¥ Ï∫îÎ≤ÑÏä§ ÏßÄÏö∞Í∏∞
        context.drawImage(img, 0, 0, canvasRef.current.width, canvasRef.current.height); // Ïù¥ÎØ∏ÏßÄ Î∞∞Í≤ΩÏúºÎ°ú Í∑∏Î¶¨Í∏∞
      };
    }
  }, [backgroundImage, context]);

  useEffect(() => {
    if (imageSrc && context) {
      const img = new Image();
      img.crossOrigin = 'Anonymous';  // CORSÎ•º ÌóàÏö©ÌïòÎäî ÏÑ§Ï†ï
      img.src = imageSrc;
      img.onload = () => {
        context.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);  // Ïù¥Ï†Ñ Í∑∏Î¶º ÏßÄÏö∞Í∏∞
        context.drawImage(img, 0, 0);  // Ïù¥ÎØ∏ÏßÄÎ•º Ï∫îÎ≤ÑÏä§Ïóê Í∑∏Î¶¨Í∏∞
      };
    }
  }, [imageSrc, context]);


  const startDrawing = (e) => {
    const { left, top } = canvasRef.current.getBoundingClientRect();
    const offsetX = e.clientX - left;  // canvasÏùò ÏôºÏ™Ω ÏÉÅÎã®ÏúºÎ°úÎ∂ÄÌÑ∞Ïùò X Ï¢åÌëú
    const offsetY = e.clientY - top;   // canvasÏùò ÏôºÏ™Ω ÏÉÅÎã®ÏúºÎ°úÎ∂ÄÌÑ∞Ïùò Y Ï¢åÌëú
    context.beginPath();
    context.moveTo(offsetX, offsetY);
    setIsDrawing(true);
  };
  
  const draw = (e) => {
    if (!isDrawing) return;
    const { left, top } = canvasRef.current.getBoundingClientRect();
    const offsetX = e.clientX - left;
    const offsetY = e.clientY - top;
    context.lineTo(offsetX, offsetY);
    context.stroke();
  };
  
  const stopDrawing = () => {
    context.closePath();
    setIsDrawing(false);
  };

  const changeColor = (newColor) => {
    setColor(newColor);
    if (context) context.strokeStyle = newColor;
  };

  const changeLineWidth = (newWidth) => {
    setLineWidth(newWidth);
    if (context) context.lineWidth = newWidth;
  };

  const enableDrawing = () => {
    setIsErasing(false); // Í∑∏Î¶¨Í∏∞ Î™®ÎìúÎ°ú ÏÑ§Ï†ï
    if (context) {
      context.globalCompositeOperation = 'source-over'; // Í∑∏Î¶¨Í∏∞ Î™®ÎìúÎ°ú ÏÑ§Ï†ï
    }
  };
  
  const enableEraser = () => {
    setIsErasing(true); // ÏßÄÏö∞Í∏∞ Î™®ÎìúÎ°ú ÏÑ§Ï†ï
    if (context) {
      context.globalCompositeOperation = 'destination-out'; // ÏßÄÏö∞Í∏∞ Î™®ÎìúÎ°ú ÏÑ§Ï†ï
    }
  };
  

  const clearCanvas = () => {
    if (!context || !canvasRef.current) return;
    context.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
  };

  const saveImage = async () => {
    if (!canvasRef.current) return;
    
    const canvas = canvasRef.current;
    const tempCanvas = document.createElement('canvas');
    const tempCtx = tempCanvas.getContext('2d');

    tempCanvas.width = canvas.width;
    tempCanvas.height = canvas.height;

    if (isWhiteBackground) {
      tempCtx.fillStyle = 'white';
      tempCtx.fillRect(0, 0, tempCanvas.width, tempCanvas.height);
    }

    tempCtx.drawImage(canvas, 0, 0);
    const dataURL = tempCanvas.toDataURL('image/png');
    let file = dataURLtoFile(dataURL, 'drawing.png');  // Data URLÏùÑ ÌååÏùºÎ°ú Î≥ÄÌôò
    try {
      const formData = new FormData();
      formData.append('file', file);
  
      await fetch('http://localhost:8081/files/upload', {  // ÏóÖÎ°úÎìúÌï† ÏÑúÎ≤Ñ API
        method: 'POST',
        body: formData,
      });
  
      alert("Ïù¥ÎØ∏ÏßÄÍ∞Ä ÏÑúÎ≤ÑÏóê Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§!");
    } catch (error) {
      console.error('Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú Ïã§Ìå®:', error);
      alert("Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú Ïã§Ìå®!");
    }

    const link = document.createElement('a');
    link.href = dataURL;
    link.download = `${fileName || 'my_drawing'}.png`;
    link.click();
  };
  const dataURLtoFile = (dataURL, filename) => {
    let arr = dataURL.split(','),  // ',' Í∏∞Ï§ÄÏúºÎ°ú Î¨∏ÏûêÏó¥ Î∂ÑÎ¶¨
        mime = arr[0].match(/:(.*?);/)[1],  // MIME ÌÉÄÏûÖ Ï∂îÏ∂ú
        bstr = atob(arr[1]),  // Base64 Î¨∏ÏûêÏó¥ÏùÑ ÎîîÏΩîÎî©
        n = bstr.length,
        u8arr = new Uint8Array(n);  // Uint8ArrayÎ°ú Î≥ÄÌôò
  
    while(n--) {
      u8arr[n] = bstr.charCodeAt(n);
    }
  
    // Î∞òÌôòÌï† ÌååÏùº Í∞ùÏ≤¥ ÏÉùÏÑ±
    return new File([u8arr], filename, {type: mime});
  };
  return (
    <div className="canvas-container">
      <canvas
        ref={canvasRef}
        width={1000}  // Ï∫îÎ≤ÑÏä§Ïùò Ïã§Ï†ú ÌÅ¨Í∏∞
        height={500}  // Ï∫îÎ≤ÑÏä§Ïùò Ïã§Ï†ú ÌÅ¨Í∏∞
        style={{ width: '1000px', height: '500px' }}  // ÌôîÎ©¥Ïóê ÌëúÏãúÎêòÎäî ÌÅ¨Í∏∞
        onMouseDown={startDrawing}
        onMouseMove={draw}
        onMouseUp={stopDrawing}
        onMouseOut={stopDrawing}
        />
      <div className="toolbar">
        <div className="toolbar-left">
          {/* ÏÉâÏÉÅ ÏÑ†ÌÉù */}
          <input type="color" value={color} onChange={(e) => changeColor(e.target.value)} />

          {/* Í∑∏Î¶¨Í∏∞ & ÏßÄÏö∞Í∞ú Î≤ÑÌäº */}
          <button onClick={enableDrawing} disabled={isErasing}>‚úèÔ∏è Í∑∏Î¶¨Í∏∞</button>
          <button onClick={enableEraser} disabled={!isErasing}>üßº ÏßÄÏö∞Í∞ú</button>


          {/* Ï¥àÍ∏∞Ìôî Î≤ÑÌäº (Ï†ÑÏ≤¥ ÏßÄÏö∞Í∏∞) */}
          <button onClick={clearCanvas}>üóëÔ∏è Ï¥àÍ∏∞Ìôî</button>
        </div>
        
        <div className="toolbar-right">
          {/* Î∞∞Í≤Ω ÏÑ†ÌÉù */}
          <label>
            <input
              type="checkbox"
              checked={isWhiteBackground}
              onChange={(e) => setIsWhiteBackground(e.target.checked)}
            />
            Î∞∞Í≤ΩÏùÑ Ìù∞ÏÉâÏúºÎ°ú Ï†ÄÏû•
          </label>

          {/* ÌååÏùºÎ™Ö ÏûÖÎ†• */}
          <input
            type="text"
            value={fileName}
            onChange={(e) => setFileName(e.target.value)}
            placeholder="ÌååÏùºÎ™ÖÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
          />

          {/* Ï†ÄÏû• Î≤ÑÌäº */}
          <button onClick={saveImage}>Ï†ÄÏû•</button>
        </div>
        
        <div className="toolbar-line-width">
          {/* ÏÑ† ÍµµÍ∏∞ Ï°∞Ï†à (ÏÑ∏Î°ú Î∞©Ìñ•) */}
          <input
            type="range"
            min="1"
            max="20"
            value={lineWidth}
            onChange={(e) => changeLineWidth(e.target.value)}
            className="vertical-slider"
          />
        </div>
      </div>
    </div>
  );
};

export default Canvas;
